count = 1
total_count = 0
row_length = len(grid)
col_length = len(grid[0])

while(count>0):
    
    new_grid = []
    count=0

    for r in range(row_length):
        new_row = []

        for c in range(col_length):
            
            if(grid[r][c] == '.'):
                new_row.append('.')
                continue
            
            adj_count = 0
            nr = 0
            nc = 0

            for i in range(-1, 2, 1):
                for j in range(-1, 2, 1):

                    if i == 0 and j == 0:
                        continue

                    nr = r+i
                    nc = c+j

                    if 0<=nr<row_length and 0<=nc<col_length:
                        if(grid[nr][nc] == '@'):
                            adj_count +=1
            
            if not adj_count > 3:
                new_row.append('.')
                count+=1
            
            else:
                new_row.append('@')

        new_grid.append(''.join(new_row))
    grid = new_grid
    total_count+=count

print(count, total_count)


                  
